te \n// ------------         \n.dropdown-menu .active > a,\n.dropdown-menu .active > a:hover {\n  background-color: @linkColor;\n  #gradient > .vertical(@linkColor, darken(@linkColor, 5%));\n}  \n\n/////////////////////////////////////////////////////\n// Styling for the Download button on the landing page\n.btn-download {\n  color: #fff;\n  background-color: @linkColor;\n  background: @linkColor;\n  &:hover {\n      background-color: darken( @linkColor, 20% );\n      background: darken( @linkColor, 20% );\n      color: #fff;\n    }\n}\n\n/////////////////////////////////////////////////////\n// Styling for the Read More button in the hero slides\n.btn-read-more {\n    margin-top: 30px;\n    @media only screen and (max-width : 1024px) {\n      margin-top: 0px;\n    }\n    @media only screen and (max-width : 720px) {\n      margin-top: -30px;\n    }\n    &:hover {\n      background-color: darken( @btnBackground, 20% );\n      background: darken( @btnBackground, 20% );\n    }\n}\n\n/////////////////////////////////////////////////////\n// Add right-hand vertical separator bar to all but\n// the right-most widget on bottom of landing page. \n.landing-page-rhs-bar {\n  min-height: 200px;\n  border-right: thin solid @lightGray;\n  padding-right: 15px;\n  padding-left: 15px;\n}\n.contributor-row {\n  padding-bottom: 30px;\n}\n// Don\'t show that bar on mobile (because the items are stacked vertically)\n@media only screen and (max-width : @resPortraitTablet) {\n  .landing-page-rhs-bar {\n    border-right: none;\n  }\n  // Also add some space underneath to separate row elements when they are stacked vertically\n  .pl-section-pad .row:last-child > [class*=\"span\"] {\n    margin-bottom: 15px;\n  }\n  .contributor-row {\n    padding-bottom: 0px;\n  }\n}\n// Override default margins for span4, which is used in the landing page\n.columnizer > .span4 {\n  margin-left: auto;\n}\n\n\n/////////////////////////////////////////////////////\n// Styling for text in text widgets at bottom of\n// landing page.\n.widgettext {\n  font-size: 125%;\n  color: @textColor;\n}\n\n/////////////////////////////////////////////////////\n// Change display of titles in widgets (used in \n// the three-column bottom half of the landing page)\n.widgettitle {\n  color: @linkColor;\n  font-size: 175%;\n}\n\n// Display of subtitle elements on some pages\n.widgetsubtitle {\n  color: @linkColor;\n  font-size: 150%;\n}\n// Remove space from above page titles\nh2.widgettitle {\n  margin-top: 0px;\n}\n// Remove space from above page titles\n.section-textbox .pl-section-pad .textbox-wrap {\n  padding-top: 0px;\n}\n\n/////////////////////////////////////////////////////\n// Remove space underneath menu title in sidebar\n.widget {\n  .widgettitle {\n    margin-bottom: 0px;\n    padding-bottom: 0px;\n  }\n}\n\n/////////////////////////////////////////////////////\n// Styling for the footer elements\n.footer {\n  color: #b3b3b3;\n  a:link {\n    color: #b3b3b3;\n  }\n  a:visited {\n    color: #b3b3b3;\n  }\n}\n\n/////////////////////////////////////////////////////\n// Used to disable text body display in hero slides\n// on mobile devices.\n.flex-caption-body {\n  @media only screen and (max-width : 1024px) {\n    display: none;\n  }\n}\n.flex-caption-title {\n  font-size:xx-large;\n  @media only screen and (max-width : 1024px) {\n    font-size: x-large;\n  }\n  @media only screen and (max-width : @resPortraitTablet) {\n    font-size: medium;\n  }\n}\n\n\n/////////////////////////////////////////////////////\n// Side navbar styling, same for each sidebar\n.side-navbar-style {\n  margin: 0px;\n  li {\n    color: @textColor;\n    font-size: 125%;\n    a, a:link {\n      color: @textColor;\n    }\n    a:hover {\n      box-shadow: inset 0 -2px 0 @linkColor;\n    }\n  }\n}\nul#menu-side-navbar-about {\n  .side-navbar-style;\n}\nul#menu-side-navbar-why-ros {\n  .side-navbar-style;\n}\nul#menu-side-navbar-getting-started {\n  .side-navbar-style;\n}\nul#menu-side-navbar-get-involved {