INSERT INTO `wp_options` VALUES (418391,'_transient_plapi_draft_core_raw','a:2:{s:8:\"sections\";s:39539:\".section-postauthor{\n	margin-bottom: 2em;\n	text-align: left;\n	.copy-pad {\n		padding:  10px 15px;\n	}\n	.author-note{\n		.noteText();\n	}\n	.subtext{\n		font-size: 86%;\n		color: @textColorAlt;\n	}\n}\n.section-mediabox {\n	\n	.pl-section-pad{\n		padding: 0;\n	}\n	.mediabox-wrap{\n		min-height: 50px;\n	//	overflow: hidden;\n		.background-size(cover);\n		\n	}\n\n	img,\n	iframe,\n	object, \n	embed{\n		max-width: 100%;\n		max-height: 100%;\n	}\n	\n	\n	\n}.pl-sharebar-pad{\n	line-height: 20px;\n	padding: 10px;\n	text-align: center;\n}\n.pl-sharebar-text{\n	.noteText();\n}\n\n.meta-share{\n	padding-top: 5px;\n	iframe, object{\n		width: auto;\n	}\n}\n\n.fb-like{\n	margin-right: 20px;\n}\n\n.pin_wrap {\n	display: inline-block;\n}.popthumbs-wrap{\n	text-align: center;\n	.popthumb{\n		width: 100%; \n		max-width: 200px; \n		display: inline-block; \n		position: relative;\n		.expander{\n			position: absolute; \n			\n			border: 2px solid #fff; \n			background: rgba(0,0,0,.3);\n			box-shadow: 0 1px 2px rgba(0,0,0,.3);\n			.border-radius(100px);\n			font-size: 20px;\n			padding: 10px;\n			text-align: center;\n			top: -17px; \n			right: -17px;\n			height: 17px;\n			width: 17px;\n			color: #fff; \n			line-height: 17px;\n		}\n		background: #fff; \n		padding: 4px; \n		.border-radius(5px);\n		box-shadow: 0 1px 3px rgba(0,0,0,.3);\n		margin-bottom: 8px;\n		&:hover{\n			opacity: .8;\n		}\n		\n	}\n	.popthumb-text{\n		padding: 8px 0;\n		.popthumb-desc{\n			opacity: .75;\n		}\n	}\n}\n/* PAGELINES STYLES */\n .revslider-container{\n	width:100% !important;\n	position:relative;\n	padding:0;\n	max-height:480px !important;\n	overflow:hidden;\n 	.tp-thumbs {\n		padding:3px;\n	}\n\n	.text-center{\n		text-align: center;\n\n	}\n	/* Text Sizing*/\n	.slider-content{\n		font-family: @plHeaderFont;\n		text-shadow: 0 -1px 0 rgba(0,0,0,.7);\n		font-size: 34px;\n		line-height: 57px;\n		&.left-side,\n		&.right-side{\n			max-width: 400px;\n			text-align: left;\n		}\n		&.centered{\n			text-align: center;\n		}\n\n		h2{\n			font-size: inherit;\n			line-height: inherit;\n			.slider-text {\n				padding: 4px 0px;\n				background: rgba(0, 0, 0, 0.5);\n				box-shadow: 10px 0 0 rgba(0, 0, 0, 0.5), -10px 0 0 rgba(0, 0, 0, 0.5);\n				color: #fff;\n\n				margin-bottom: 1px;\n				background-position: right -53px;\n				.transition(all 0.3s linear);\n				margin-bottom: 15px;\n			}\n		}\n		a.slider-btn {\n			font-size: 13px;\n			line-height: 1.3em;\n			color: #eee;\n			background: rgba(0, 0, 0, 0.5);\n			left: -10px;\n			top: 10px;\n			position: relative;\n			padding: 10px 18px;\n			.transition(all 0.3s linear);\n			&:hover{\n				color:#fff;\n				background: @linkColor;\n			}\n		}\n		&.slider-black-text{\n			.slider-text{\n				background: rgba(255, 255, 255, 0.7);\n				box-shadow: 10px 0 0 rgba(255, 255, 255, 0.7), -10px 0 0 rgba(255, 255, 255, 0.7);\n				text-shadow: 0 1px 0 #fff;\n				color:#000;\n			}\n			a.slider-btn{\n				background: #fff;\n				color: #000;\n					text-shadow: 0 1px 0 #fff;\n				opacity: .6;\n				&:hover{\n					opacity: 1;\n				}\n			}\n		}\n	}\n\n\n\n\n}\n\n\n/* SLIDER STYLES */\n\n.header-shadow{\n	background:  url(\"@{plRoot}/sections/revslider/images/header-shadow.png\") no-repeat center top;\n	background-size: 100% auto;\n	width: 100%;\n	height: 30px;\n	position: absolute;\n	top: 0;\n	left: 0;\n	z-index: 27;\n}\n\n.tp-hide-revslider,\n.tp-caption.tp-hidden-caption{\n	visibility:hidden !important;\n	display:none !important;\n}\n\n.tp-caption {\n	z-index:1;\n	&.boxshadow{\n		box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.5);\n	}\n	&.noshadow {\n		text-shadow: none;\n	}\n	a {\n		.transition(all 0.2s ease-out);\n	}\n}\n\n.tp_inner_padding{\n	.box-sizing( border-box );\n	max-height:none !important;\n}\n\n\n/****************************************************************\n\n	-	SET THE ANIMATION EVEN MORE SMOOTHER ON ANDROID   -\n\n******************************************************************/\n.tp-simpleresponsive{\n	.slotholder *,\n	.tp-simpleresponsive img{\n		-webkit-transform: translateZ(0);\n		-webkit-backface-visibility: hidden;\n		-webkit-perspective: 1000;\n	}\n	caption,\n	.tp-caption {\n		opacity: 0;\n		position:absolute;\n		visibility: hidden;\n\n	}\n}\n\n\n/******************************\n	-	IE8 HACKS	-\n*******************************/\n.noFilterClass {\n	filter:none !important;\n}\n\n\n/******************************\n	-	SHADOWS		-\n******************************/\n.tp-bannershadow  {\n	position:absolute;\n\n	margin-left:auto;\n	margin-right:auto;\n	.user-select(none);\n\n}\n\n\n\n/********************************\n	-	FULLSCREEN VIDEO	-\n*********************************/\n.caption,\n.tp-caption{\n	&.fullscreenvideo {\n		left:0px;\n		top:0px;\n		position:absolute;\n		width:100%;\n		height:100%;\n		iframe	{ width:100% !important; height:100% !important;}\n	}\n}\n\n/************************\n	-	NAVIGATION	-\n*************************/\n\n\n.tpclear{clear:both;}\n\n.tp-bullets	{\n	z-index:1000;\n	position:absolute;\n	opacity: 1;\n	.transition(all 0.2s ease-out);\n	margin-bottom: 0px;\n	&.hidebullets	{\n		margin-bottom: -10px;\n		opacity: 0;\n	}\n	&.simplebullets.round .bullet{\n		cursor: pointer;\n		position: relative;\n		background: rgba(0,0,0,.6);\n		width: 8px;\n		height: 8px;\n		margin-right: 0px;\n		float: left;\n		margin-top: 0px;\n		margin-left: 5px;\n		.border-radius(8px);\n		border: 2px solid #fff;\n		opacity: .7;\n		.trans\n		&.last{\n			margin-right:3px;\n		}\n		&.selected,\n		&:hover{\n			opacity: 1;\n		}\n	}\n}\n\n/*************************************\n	-	TP ARROWS 	-\n**************************************/\n.tparrows{\n	opacity: 1;\n	.transition(opacity 0.2s ease-out);\n	&.hidearrows{\n		opacity: 0;\n	}\n}\n.tp-leftarrow,\n.tp-rightarrow{\n	cursor:pointer;\n	position:relative;\n	z-index:100;\n	width:40px;\n	height:40px;\n	&:hover{\n		background-position:bottom left;\n		opacity: .7;\n	}\n	&.default{\n\n	}\n}\n.tp-leftarrow{\n	background:url(\"@{plRoot}/sections/revslider/assets/large_left.png\") no-repeat top left;\n\n}\n.tp-rightarrow{\n	background:url(\"@{plRoot}/sections/revslider/assets/large_right.png\") no-repeat top left;\n}\n\n\n/****************************************************************************************************\n	-	TP THUMBS 	-\n*****************************************************************************************************\n\n - tp-thumbs & tp-mask Width is the width of the basic Thumb Container (500px basic settings)\n\n - .bullet width & height is the dimension of a simple Thumbnail (basic 100px x 50px)\n\n *****************************************************************************************************/\n\n\n.tp-bullets.tp-thumbs{\n	z-index:1000;\n	position:absolute;\n	padding:3px;\n	background-color:#fff;\n	width:500px;\n	height:50px; 			/* THE DIMENSIONS OF THE THUMB CONTAINER */\n	margin-top:-50px;\n	.tp-mask{\n		width:500px;\n		height:50px;  			/* THE DIMENSIONS OF THE THUMB CONTAINER */\n		overflow:hidden;\n		position:relative;\n		.tp-thumbcontainer	{	width:5000px; position:absolute;}\n	}\n	.bullet{\n		width:100px;\n		height:50px;\n		cursor:pointer;\n		overflow:hidden;\n		background:none;\n		margin:0;\n		float:left;\n		opacity: 0.5;\n		.transition(all 0.2s ease-out);\n		&:hover,\n		&.selected{\n			opacity: 1;\n		}\n	}\n	img{\n		max-width:100%;\n	}\n}\n\n\n\n\n\n/************************************\n		-	TP BANNER TIMER		-\n*************************************/\n.tp-bannertimer	{\n	width:100%;\n	height:10px;\n	background:url(\"@{plRoot}/sections/revslider/assets/timer.png\");\n	position:absolute;\n	z-index:200;\n	&.tp-bottom{\n		bottom:0px !important;\n		height:5px;\n	}\n}\n\n@media only screen and (min-width: 480px) and (max-width: 767px) {\n	.responsive .tp-bullets.tp-thumbs{\n		width:300px !important;\n		height:30px !important;\n		.tp-mask{\n			width:300px !important; height:30px !important;\n		}\n		.bullet{